import React, {useState} from "react";

function ToDoList() {
const [tasks, setTasks] = useState([]);
const [newTask, setNewTasks] = useState ("");

function handleInputChange(event){
    setNewTasks(event.target.value);

}

function handleAddTask(event){
    if(newTask.trim() !== ""){
        setTasks(t => [...t, newTask])
    setNewTasks("");
    }

}

function handleDeleteTask(index){
    setTasks(t => t.filter((_, i) => i !== index));
}

function handleCheckTask(index){
    setTasks(t => t.map((tasks, i) => i === index ? {...tasks, completed : !tasks.completed} : tasks))

}
return(
    <div>
        <input type ="text"
                value ={newTask}
                placeholder="Please enter your task"
                onChange={handleInputChange}/>
        <button onClick={handleAddTask}>
                Add
            </button>

        <ul> {tasks.map((tasks, index) => 
        <li key = {index} style ={{color: tasks.completed ? "gray" : "black"}}>
            <input 
                        type="checkbox"
                        checked = {tasks.completed}
                        onChange={() => handleCheckTask(index)}/>
            {tasks}
            <button onClick={() => handleDeleteTask(index)}>
                Delete
            </button>
        </li>
            )}


        </ul>
    </div>
)
}

export default ToDoList;
